// =================================================================
// 10. 上書き: frontend/src/components/AmbulanceCallComponent.js
// =================================================================
import React, { useState } from 'react';
import { useSettings } from '../context/SettingsContext';

const AmbulanceCallComponent = ({ step, onNextAction }) => {
    const { settings } = useSettings();
    const [isCalling, setIsCalling] = useState(false);
    const [callFinished, setCallFinished] = useState(false);

    const handleCallClick = () => {
        setIsCalling(true);
        setTimeout(() => {
            setIsCalling(false);
            setCallFinished(true);
        }, 3000);
    };

    return (
        <div className="screen-container ambulance-call-container">
            <h2>{step.message}</h2>

            {!callFinished && (
                isCalling ? (
                    <div className="calling-message" style={{padding: '1rem', border: '1px solid #ccc'}}>通話中...</div>
                ) : (
                    <button onClick={handleCallClick} className="ambulance-call-button" style={{padding: '1rem', border: '1px solid'}}>119番に発信する</button>
                )
            )}

            <div className="call-script">
                <p><strong>「救急です。」</strong></p>
                <p>「住所は {settings.address || '（未設定）'} です」</p>
                <p>「電話番号は {settings.phoneNumber || '（未設定）'} です」</p>
                <p>「男性（女性）、〇〇歳くらいです」</p>
            </div>

            <div className="audio-input-section">
                <p>「何がどうなっていますか？」下のマイクボタンを押してお答えください。</p>
                <button className="audio-button">
                    <svg className="mic-icon" xmlns="[http://www.w3.org/2000/svg](http://www.w3.org/2000/svg)" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M12 18.75a6 6 0 006-6v-1.5m-6 7.5a6 6 0 01-6-6v-1.5m6 7.5v3.75m-3.75 0h7.5M12 15.75a3 3 0 01-3-3V4.5a3 3 0 016 0v8.25a3 3 0 01-3 3z" />
                    </svg>
                </button>
                <div className="transcribed-text">
                    <p>（ここにAIで要約された文章が表示されます）</p>
                </div>
            </div>

            {callFinished && (
                <button onClick={onNextAction} className="next-action-button">
                    応急処置にうつる
                </button>
            )}
        </div>
    );
};

export default AmbulanceCallComponent;
