// =================================================================
// 8. 上書き: frontend/src/components/EmergencyScreen.js
// =================================================================
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import QuestionComponent from './QuestionComponent';
import AmbulanceCallComponent from './AmbulanceCallComponent';
import InstructionComponent from './InstructionComponent';
import FinalScreen from './FinalScreen';

const API_BASE_URL = 'http://localhost:8000';

// ダミーのシナリオデータ（バックエンドと同期させる）
const DUMMY_SCENARIO_DATA = {
    "heatstroke_instructions": {
        "id": "heatstroke_instructions",
        "step_type": "INSTRUCTION",
        "message": "熱中症の応急処置",
    }
}

const EmergencyScreen = () => {
    const [currentStep, setCurrentStep] = useState(null);
    const [history, setHistory] = useState(['start']);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchStep(history[history.length - 1]);
    }, [history]);

    const fetchStep = async (stepId) => {
        setIsLoading(true);
        setError(null);
        try {
            // ダミーデータとマージ
            const response = await axios.get(`${API_BASE_URL}/api/step/${stepId}`);
            setCurrentStep({ ...response.data, ...DUMMY_SCENARIO_DATA[stepId] });
        } catch (err) {
            // APIにない場合はダミーデータから探す
            if (DUMMY_SCENARIO_DATA[stepId]) {
                setCurrentStep(DUMMY_SCENARIO_DATA[stepId]);
            } else {
                setError('データの取得に失敗しました。');
                console.error(err);
            }
        } finally {
            setIsLoading(false);
        }
    };

    const handleNextStep = (nextStepId) => {
        setHistory(prev => [...prev, nextStepId]);
    };

    const handleGoBack = () => {
        if (history.length > 1) {
            setHistory(prev => prev.slice(0, -1));
        }
    };

    const renderCurrentStep = () => {
        if (isLoading) return <div>読み込み中...</div>;
        if (error) return <div className="error">{error}</div>;
        if (!currentStep) return null;

        switch (currentStep.step_type) {
            case 'QUESTION':
                return <QuestionComponent step={currentStep} onOptionSelect={handleNextStep} onGoBack={handleGoBack} />;
            case 'AMBULANCE_CALL':
                return <AmbulanceCallComponent step={currentStep} onNextAction={() => handleNextStep("heatstroke_instructions")} />;
            case 'INSTRUCTION':
                return <InstructionComponent step={currentStep} onGoBack={handleGoBack} />;
            case 'FINAL_NO_EMERGENCY':
                 return <FinalScreen step={currentStep} />;
            default:
                return <div>不明なステップタイプです。</div>;
        }
    };

    return (
        <div className="screen-container">
            {renderCurrentStep()}
        </div>
    );
};

export default EmergencyScreen;
