import React from 'react';

const AmbulanceCallComponent = ({ step }) => {
    // symptom変数は、このコンポーネントが表示された時点で確定している
    const symptom = step.symptom_on_arrival;

    // この後の音声認識や応急処置への遷移は、このコンポーネント内にボタンとして実装します。
    // プロトタイプではまず表示までを実装します。

    return (
        <div className="ambulance-call-container">
            <h2>{step.message}</h2>
            <div className="call-119-button-wrapper">
                <a href="tel:119" className="call-119-button">
                    119
                    <span>救急車に連絡する</span>
                </a>
            </div>
            <div className="call-script">
                <p><strong>「救急です。」</strong></p>
                <p>「住所は〇〇（施設の住所）です」</p>
                <p>「男性（女性）、〇〇歳くらいです」</p>
                {/* 音声認識結果はここに表示 */}
            </div>
            {/* symptom変数が確定したら表示するボタン */}
            {(symptom === 'Heat_Stroke' || symptom === 'Others') && (
                <button className="next-action-button">
                    応急処置・次の行動を行う
                </button>
            )}
        </div>
    );
};

export default AmbulanceCallComponent;
