// =================================================================
// 4. 上書き: frontend/src/components/AmbulanceCallComponent.js
// (安定性向上のための安全装置を追加)
// =================================================================
import React, { useState, useRef, useEffect } from 'react';
import { useSettings } from '../context/SettingsContext';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000';

const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

const AmbulanceCallComponent = ({ step, onNextAction }) => {
    const { settings } = useSettings();
    const [isRecording, setIsRecording] = useState(false);
    const [transcript, setTranscript] = useState('');
    const [summary, setSummary] = useState('');
    const [isSummarizing, setIsSummarizing] = useState(false);
    const [error, setError] = useState('');

    const recognitionRef = useRef(null);

    // === 安全装置: stepデータがなければクラッシュしない ===
    if (!step) {
        return null;
    }

    useEffect(() => {
        if (!SpeechRecognition) {
            setError("お使いのブラウザは音声認識をサポートしていません。");
            return;
        }
        recognitionRef.current = new SpeechRecognition();
        const recognition = recognitionRef.current;
        recognition.continuous = true;
        recognition.lang = 'ja-JP';
        recognition.interimResults = true;

        recognition.onresult = (event) => {
            let finalTranscript = '';
            for (let i = event.resultIndex; i < event.results.length; i++) {
                if (event.results[i].isFinal) {
                    finalTranscript += event.results[i][0].transcript;
                }
            }
            if (finalTranscript) {
                 setTranscript(prev => prev + finalTranscript + '。');
            }
        };

        recognition.onend = () => setIsRecording(false);
        recognition.onerror = (event) => setError(`音声認識エラー: ${event.error}`);

        return () => {
          if (recognitionRef.current) {
            recognitionRef.current.stop();
          }
        }
    }, []);

    const toggleRecording = () => {
        if (!recognitionRef.current) return;
        if (isRecording) {
            recognitionRef.current.stop();
        } else {
            setTranscript('');
            setSummary('');
            setError('');
            recognitionRef.current.start();
            setIsRecording(true);
        }
    };

    const handleSummarize = async () => {
        if (!transcript) return;
        setIsSummarizing(true);
        setError('');
        try {
            const response = await axios.post(`${API_BASE_URL}/api/summarize`, { text: transcript });
            setSummary(response.data.summary);
        } catch (err) {
            console.error("要約エラー:", err);
            setError("AIによる要約に失敗しました。サーバーが起動しているか、APIキーが正しいか確認してください。");
        } finally {
            setIsSummarizing(false);
        }
    };

    return (
        <div className="screen-container">
            <h2 className="screen-header" style={{color: 'var(--danger-color)'}}>{step.message}</h2>

            <div className="call-script">
                <p><strong>「救急です。」</strong></p>
                <p>「住所は {settings.address || '(未設定)'} です」</p>
                <p>「電話番号は {settings.phoneNumber || '(未設定)'} です」</p>
            </div>

            <div className="audio-input-section">
                <p style={{fontSize: 'var(--font-size-md)'}}>「何がどうなっていますか？」</p>
                <button onClick={toggleRecording} className={`audio-button ${isRecording ? 'is-recording' : ''}`}>
                    <svg className="mic-icon" xmlns="[http://www.w3.org/2000/svg](http://www.w3.org/2000/svg)" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M12 18.75a6 6 0 006-6v-1.5m-6 7.5a6 6 0 01-6-6v-1.5m6 7.5v3.75m-3.75 0h7.5M12 15.75a3 3 0 01-3-3V4.5a3 3 0 016 0v8.25a3 3 0 01-3 3z" />
                    </svg>
                </button>
                <p>{isRecording ? "認識中... もう一度押すと停止" : "マイクボタンを押して話してください"}</p>

                <div className="transcribed-text">
                    <strong>あなたの発言:</strong>
                    <p>{transcript || "（ここに音声認識の結果が表示されます）"}</p>
                </div>
                <button className="button" onClick={handleSummarize} disabled={isSummarizing || !transcript} style={{width:'auto', padding:'0.8rem 2rem', marginTop:'1rem'}}>
                    {isSummarizing ? "AIが要約中..." : "AIで要約する"}
                </button>
                <div className="summary-text">
                    <strong>AIによる要約:</strong>
                    <p>{summary || "（ここに救急隊員に伝えるための要約文が表示されます）"}</p>
                </div>
                 {error && <p style={{color: 'red', marginTop: '1rem'}}>{error}</p>}
            </div>

            <div className="footer-actions">
                <button onClick={onNextAction} className="button button-success">応急処置にうつる</button>
            </div>
        </div>
    );
};

export default AmbulanceCallComponent;
