// =================================================================
// 1. ファイル名: frontend/src/components/FinalScreen.js (新規作成)
// =================================================================
import React from 'react';

const FinalScreen = ({ step }) => {
    const goHome = () => {
        // This is a simple way to reload the app to go home in this context.
        // In a full app, you'd use navigate('/') from react-router-dom.
        window.location.href = "/";
    };

    return (
        <div className="flex flex-col h-full p-4 items-center justify-center text-center">
            <h2 className="text-4xl font-bold text-gray-800">{step.message}</h2>
            <p className="text-xl text-gray-600 mt-4">必要に応じて、かかりつけ医や近隣の医療機関にご相談ください。</p>
            <div className="mt-8 flex space-x-4">
                <div className="text-center">
                    <p className="font-bold mb-2">医療情報ネット</p>
                    {/* In a real app, you'd use a QR code library */}
                    <div className="w-40 h-40 bg-gray-200 flex items-center justify-center rounded-lg">QRコード</div>
                </div>
                <div className="text-center">
                    <p className="font-bold mb-2">全国タクシーガイド</p>
                    <div className="w-40 h-40 bg-gray-200 flex items-center justify-center rounded-lg">QRコード</div>
                </div>
            </div>
            <button
                onClick={goHome}
                className="mt-12 bg-blue-600 text-white font-bold py-4 px-10 rounded-lg text-xl hover:bg-blue-700 transition-colors">
                ホーム画面に戻る
            </button>
        </div>
    );
};

export default FinalScreen;
