// =================================================================
// 4. ファイル名: frontend/src/components/AmbulanceCallComponent.js (上書き)
// =================================================================
import React, { useState } from 'react';

const AmbulanceCallComponent = ({ step }) => {
    const [isCalling, setIsCalling] = useState(false);
    const [callFinished, setCallFinished] = useState(false);

    const handleCallClick = () => {
        setIsCalling(true);
        // 3秒間の通話をシミュレート
        setTimeout(() => {
            setIsCalling(false);
            setCallFinished(true);
        }, 3000);
    };

    const symptom = step.symptom_on_arrival;

    return (
        <div className="screen-container ambulance-call-container">
            <h2>{step.message}</h2>

            {!callFinished && (
                isCalling ? (
                    <div className="calling-message">通話中...</div>
                ) : (
                    <button onClick={handleCallClick} className="ambulance-call-button">
                        119番に発信する
                    </button>
                )
            )}

            {callFinished && (
                <>
                    <div className="call-script">
                        <p><strong>「救急です。」</strong></p>
                        <p>「住所は〇〇（施設の住所）です」</p>
                        <p>「男性（女性）、〇〇歳くらいです」</p>
                    </div>

                    {(symptom === 'Heat_Stroke' || symptom === 'Others') && (
                        <button className="next-action-button">
                            応急処置・次の行動を行う
                        </button>
                    )}
                </>
            )}
        </div>
    );
};

export default AmbulanceCallComponent;
