// =================================================================
// 3. 新規ファイル: frontend/src/components/InstructionComponent.js
// =================================================================
import React from 'react';
import { useNavigate } from 'react-router-dom';

const InstructionComponent = ({ step, onGoBack }) => {
    const navigate = useNavigate();

    return (
        <div className="screen-container instruction-container">
            <h2 className="instruction-title">{step.message}</h2>
            <div className="instruction-content">
                <p>（ここに具体的な応急処置の内容が表示されます）</p>
                <ul>
                    <li>涼しい場所へ避難</li>
                    <li>衣服をゆるめる</li>
                    <li>体を冷やす</li>
                    <li>水分補給</li>
                </ul>
            </div>
            <div className="form-actions">
                <button onClick={onGoBack} className="button-back">戻る</button>
                <button onClick={() => navigate('/')} className="button-home">ホーム画面に戻る</button>
            </div>
        </div>
    );
};

export default InstructionComponent;
