// =================================================================
// 8. 上書き: frontend/src/components/EmergencyScreen.js
// =================================================================
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import QuestionComponent from './QuestionComponent';
import AmbulanceCallComponent from './AmbulanceCallComponent';
import InstructionComponent from './InstructionComponent';
import FinalScreen from './FinalScreen';

const API_BASE_URL = 'http://localhost:8000';

// ダミーのシナリオデータ（バックエンドと同期させる）
const DUMMY_SCENARIO_DATA = {
    "start": {
        "id": "start",
        "step_type": "QUESTION",
        "question_text": "緊急事態の種類を選択してください",
        "options": [
            {
                "text": "熱中症の疑い",
                "next_step_id": "heatstroke_question"
            },
            {
                "text": "その他の症状",
                "next_step_id": "other_symptoms"
            }
        ]
    },
    "heatstroke_question": {
        "id": "heatstroke_question",
        "step_type": "QUESTION",
        "question_text": "意識はありますか？",
        "options": [
            {
                "text": "意識がある",
                "next_step_id": "conscious_heatstroke"
            },
            {
                "text": "意識がない",
                "next_step_id": "unconscious_heatstroke"
            }
        ]
    },
    "conscious_heatstroke": {
        "id": "conscious_heatstroke",
        "step_type": "INSTRUCTION",
        "message": "涼しい場所に移動し、水分を補給してください。症状が改善しない場合は救急車を呼んでください。"
    },
    "unconscious_heatstroke": {
        "id": "unconscious_heatstroke",
        "step_type": "AMBULANCE_CALL",
        "message": "救急車を呼びます。"
    },
    "other_symptoms": {
        "id": "other_symptoms",
        "step_type": "QUESTION",
        "question_text": "どのような症状がありますか？",
        "options": [
            {
                "text": "胸の痛み",
                "next_step_id": "chest_pain"
            },
            {
                "text": "呼吸困難",
                "next_step_id": "breathing_difficulty"
            },
            {
                "text": "その他",
                "next_step_id": "other_emergency"
            }
        ]
    },
    "heatstroke_instructions": {
        "id": "heatstroke_instructions",
        "step_type": "INSTRUCTION",
        "message": "熱中症の応急処置",
    }
}

const EmergencyScreen = () => {
    const [currentStep, setCurrentStep] = useState(null);
    const [history, setHistory] = useState(['start']);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        console.log('EmergencyScreen: useEffect triggered, stepId:', history[history.length - 1]);
        fetchStep(history[history.length - 1]);
    }, [history]);

    const fetchStep = async (stepId) => {
        console.log('EmergencyScreen: fetchStep called with stepId:', stepId);
        setIsLoading(true);
        setError(null);
        try {
            // まずダミーデータをチェック
            if (DUMMY_SCENARIO_DATA[stepId]) {
                console.log('EmergencyScreen: Using dummy data for stepId:', stepId);
                setCurrentStep(DUMMY_SCENARIO_DATA[stepId]);
                setIsLoading(false);
                return;
            }

            // バックエンドAPIからデータを取得
            console.log('EmergencyScreen: Fetching from API for stepId:', stepId);
            const response = await axios.get(`${API_BASE_URL}/api/step/${stepId}`);
            setCurrentStep(response.data);
        } catch (err) {
            console.error('API Error:', err);
            // APIにない場合はダミーデータから探す
            if (DUMMY_SCENARIO_DATA[stepId]) {
                console.log('EmergencyScreen: Using dummy data as fallback for stepId:', stepId);
                setCurrentStep(DUMMY_SCENARIO_DATA[stepId]);
            } else {
                setError('データの取得に失敗しました。');
                console.error(err);
            }
        } finally {
            setIsLoading(false);
        }
    };

    const handleNextStep = (nextStepId) => {
        setHistory(prev => [...prev, nextStepId]);
    };

    const handleGoBack = () => {
        if (history.length > 1) {
            setHistory(prev => prev.slice(0, -1));
        }
    };

    const renderCurrentStep = () => {
        console.log('EmergencyScreen: renderCurrentStep called', {
            isLoading,
            error,
            currentStep,
            stepType: currentStep?.step_type
        });

        if (isLoading) {
            return (
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>読み込み中...</p>
                </div>
            );
        }

        if (error) {
            return (
                <div className="error-container">
                    <h2>エラーが発生しました</h2>
                    <p>{error}</p>
                    <button onClick={() => window.location.reload()}>再読み込み</button>
                </div>
            );
        }

        if (!currentStep) {
            return (
                <div className="error-container">
                    <h2>データが見つかりません</h2>
                    <p>現在のステップのデータを取得できませんでした。</p>
                </div>
            );
        }

        switch (currentStep.step_type) {
            case 'QUESTION':
                return <QuestionComponent step={currentStep} onOptionSelect={handleNextStep} onGoBack={handleGoBack} />;
            case 'AMBULANCE_CALL':
                return <AmbulanceCallComponent step={currentStep} onNextAction={() => handleNextStep("heatstroke_instructions")} />;
            case 'INSTRUCTION':
                return <InstructionComponent step={currentStep} onGoBack={handleGoBack} />;
            case 'FINAL_NO_EMERGENCY':
                 return <FinalScreen step={currentStep} />;
            default:
                return (
                    <div className="error-container">
                        <h2>不明なステップタイプ</h2>
                        <p>ステップタイプ: {currentStep.step_type}</p>
                    </div>
                );
        }
    };

    return (
        <div className="screen-container">
            {renderCurrentStep()}
        </div>
    );
};

export default EmergencyScreen;
