// =================================================================
import React from 'react';

const QuestionComponent = ({ step, onOptionSelect }) => {
    return (
        <div className="screen-container">
            <h2 className="question-header">{step.question_text || step.message}</h2>
            <div className="question-options-container">
                {step.options.map(option => (
                    <button
                        key={option.next_step_id}
                        className="question-option-button"
                        onClick={() => onOptionSelect(option.next_step_id)}
                    >
                        {option.text}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default QuestionComponent;
