import React from 'react';
import { useNavigate } from 'react-router-dom';

const StandbyScreen = () => {
    const navigate = useNavigate();

    const handleSosClick = () => {
        navigate('/emergency');
    };

    return (
        <div className="standby-container">
            <h1>緊急時対応システム</h1>
            <button className="sos-button" onClick={handleSosClick}>
                SOS
            </button>
            <div className="info-footer">
                {/* ここに時刻や暑さ指数などを表示 */}
                <p>{new Date().toLocaleString('ja-JP')}</p>
                <button className="training-button">訓練モード</button>
            </div>
        </div>
    );
};

export default StandbyScreen;
