// =================================================================
// 2. ファイル名: frontend/src/components/StandbyScreen.js (上書き)
// =================================================================
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const StandbyScreen = () => {
    const navigate = useNavigate();
    const [time, setTime] = useState(new Date());

    useEffect(() => {
        // 1秒ごとに時間を更新
        const timerId = setInterval(() => {
            setTime(new Date());
        }, 1000);
        // コンポーネントがアンマウントされたらタイマーをクリア
        return () => clearInterval(timerId);
    }, []);

    const handleSosClick = () => {
        navigate('/emergency');
    };

    // 時刻を HH:MM 形式にフォーマット
    const formattedTime = time.toLocaleTimeString('ja-JP', {
        hour: '2-digit',
        minute: '2-digit',
    });

    return (
        <div className="screen-container standby-container">
            <header className="standby-header">
                <div className="wbgt-alert">
                    <span>暑さ指数(WBGT):</span>
                    {/* API連携するまでは固定値 */}
                    <span className="value">27.5℃ (警戒)</span>
                </div>
                <div className="clock">{formattedTime}</div>
            </header>

            <main className="standby-main">
                <button className="sos-button" onClick={handleSosClick}>
                    SOS
                </button>
            </main>

            <footer className="standby-footer">
                <button className="training-button">訓練モード</button>
            </footer>
        </div>
    );
};

export default StandbyScreen;
