// =================================================================
// 5. 上書き: frontend/src/components/AmbulanceCallComponent.js
// (音声入力、AI要約機能を実装)
// =================================================================
import React, { useState, useRef, useEffect } from 'react';
import { useSettings } from '../context/SettingsContext';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000';

const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
const recognition = SpeechRecognition ? new SpeechRecognition() : null;

if (recognition) {
    recognition.continuous = true;
    recognition.lang = 'ja-JP';
    recognition.interimResults = true;
}

const AmbulanceCallComponent = ({ step, onNextAction }) => {
    const { settings } = useSettings();
    const [isCalling, setIsCalling] = useState(false);
    const [callFinished, setCallFinished] = useState(false);

    const [isRecording, setIsRecording] = useState(false);
    const [transcript, setTranscript] = useState('');
    const [summary, setSummary] = useState('');
    const [isSummarizing, setIsSummarizing] = useState(false);

    const recognitionRef = useRef(recognition);

    useEffect(() => {
        const rec = recognitionRef.current;
        if (!rec) {
            console.log("音声認識は、このブラウザではサポートされていません。");
            return;
        }

        rec.onresult = (event) => {
            let finalTranscript = '';
            for (let i = event.resultIndex; i < event.results.length; i++) {
                if (event.results[i].isFinal) {
                    finalTranscript += event.results[i][0].transcript;
                }
            }
            if (finalTranscript) {
                 setTranscript(prev => prev + finalTranscript);
            }
        };

        rec.onend = () => {
            setIsRecording(false);
        };

        return () => {
            rec.onresult = null;
            rec.onend = null;
        }
    }, []);

    const handleCallClick = () => {
        setIsCalling(true);
        setTimeout(() => {
            setIsCalling(false);
            setCallFinished(true);
        }, 3000);
    };

    const toggleRecording = () => {
        if (!recognitionRef.current) return;

        if (isRecording) {
            recognitionRef.current.stop();
        } else {
            setTranscript('');
            setSummary('');
            recognitionRef.current.start();
        }
        setIsRecording(!isRecording);
    };

    const handleSummarize = async () => {
        if (!transcript) {
            alert("要約するテキストがありません。");
            return;
        }
        setIsSummarizing(true);
        try {
            const response = await axios.post(`${API_BASE_URL}/api/summarize`, { text: transcript });
            setSummary(response.data.summary);
        } catch (error) {
            console.error("要約エラー:", error);
            setSummary("AIによる要約に失敗しました。");
        } finally {
            setIsSummarizing(false);
        }
    };


    return (
        <div className="ambulance-call-container-simple">
            <h2>{step.message}</h2>

            <div className="call-script">
                <p><strong>「救急です。」</strong></p>
                <p>「住所は {settings.address || '(設定画面で登録)'} です」</p>
                <p>「電話番号は {settings.phoneNumber || '(設定画面で登録)'} です」</p>
            </div>

            <div className="audio-input-section">
                <p>「何がどうなっていますか？」</p>
                <button onClick={toggleRecording} className={`audio-button ${isRecording ? 'is-recording' : ''}`}>
                    {/* Mic Icon */}
                </button>
                <p>{isRecording ? "音声認識中... 話し終えたらもう一度押してください" : "下のマイクボタンを押して話してください"}</p>

                <div className="transcribed-text">
                    <strong>あなたの発言:</strong>
                    <p>{transcript || "（ここに音声認識の結果が表示されます）"}</p>
                </div>

                <button onClick={handleSummarize} disabled={isSummarizing || !transcript}>
                    {isSummarizing ? "AIが要約中..." : "AIで要約する"}
                </button>

                <div className="summary-text">
                    <strong>AIによる要約:</strong>
                    <p>{summary || "（ここに救急隊員に伝えるための要約文が表示されます）"}</p>
                </div>
            </div>

            <div style={{marginTop: "auto"}}>
                <button onClick={onNextAction} className="next-action-button">
                    応急処置にうつる
                </button>
            </div>
        </div>
    );
};

export default AmbulanceCallComponent;
