// =================================================================
// 3. ファイル名: frontend/src/components/AmbulanceCallComponent.js (上書き)
// =================================================================
import React, { useState } from 'react';
import { useSettings } from '../context/SettingsContext';

const AmbulanceCallComponent = ({ step, onNextAction }) => {
    const { settings } = useSettings();
    const [isCalling, setIsCalling] = useState(false);
    const [callFinished, setCallFinished] = useState(false);

    const handleCallClick = () => {
        setIsCalling(true);
        setTimeout(() => {
            setIsCalling(false);
            setCallFinished(true);
        }, 3000);
    };

    return (
        <div className="ambulance-call-container-simple">
            <h2>{step.message}</h2>

            {!callFinished && (
                isCalling ? (
                    <div style={{ padding: '1rem', border: '1px solid #ccc', marginBottom: '2rem' }}>通話中...</div>
                ) : (
                    <button onClick={handleCallClick} style={{ padding: '1rem', border: '1px solid', marginBottom: '2rem' }}>119番に発信する</button>
                )
            )}

            <div className="call-script">
                <p><strong>「救急です。」</strong></p>
                <p>「住所は {settings.address || '（設定画面で登録してください）'} です」</p>
                <p>「電話番号は {settings.phoneNumber || '（設定画面で登録してください）'} です」</p>
                <p>「男性（女性）、〇〇歳くらいです」</p>
            </div>

            <div style={{ margin: '2rem 0', padding: '1.5rem', backgroundColor: '#f5f5f5', borderRadius: '0.5rem', textAlign: 'center' }}>
                <p>「何がどうなっていますか？」下のマイクボタンを押してお答えください。</p>
                <button style={{ backgroundColor: '#337ab7', color: 'white', border: 'none', borderRadius: '50%', width: '5rem', height: '5rem', cursor: 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center', margin: '1rem auto' }}>
                    {/* Mic Icon SVG */}
                    <svg style={{ width: '2.5rem', height: '2.5rem' }} xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M12 18.75a6 6 0 006-6v-1.5m-6 7.5a6 6 0 01-6-6v-1.5m6 7.5v3.75m-3.75 0h7.5M12 15.75a3 3 0 01-3-3V4.5a3 3 0 016 0v8.25a3 3 0 01-3 3z" />
                    </svg>
                </button>
                <div style={{ minHeight: '5rem', backgroundColor: 'white', border: '1px solid #ddd', padding: '1rem', borderRadius: '0.5rem' }}>
                    <p>（ここにAIで要約された文章が表示されます）</p>
                </div>
            </div>

            {callFinished && (
                <button onClick={onNextAction} className="next-action-button">
                    応急処置にうつる
                </button>
            )}
        </div>
    );
};

export default AmbulanceCallComponent;
