import React from 'react';

const QuestionComponent = ({ step, onOptionSelect, onGoBack }) => {
    // === 安全装置 ===
    // stepデータがまだ届いていないか、必要な情報(id, options)が欠けている場合、
    // クラッシュせずに何も表示しないようにします。
    if (!step || !step.id || !Array.isArray(step.options)) {
        return null;
    }

    const showBackButton = step.id !== 'start';

    return (
        <div className="screen-container">
            <h2 className="question-header">{step.question_text || step.message}</h2>
            <div className="question-options-container">
                {step.options.map(option => (
                    <button
                        key={option.next_step_id}
                        className="question-option-button"
                        onClick={() => onOptionSelect(option.next_step_id)}
                    >
                        {option.text}
                    </button>
                ))}
            </div>
            {showBackButton && (
                <div className="question-footer">
                    <button onClick={onGoBack} className="button-back">戻る</button>
                </div>
            )}
        </div>
    );
};

export default QuestionComponent;
